import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./QLHH.css";

const QLHH = () => {
  const navigate = useNavigate();
  const [order, setOrder] = useState([]);
  const [selectedOrder, setSelectedOrder] = useState(null); // Lưu đơn hàng đang chọn
  const [isModalOpen, setModalOpen] = useState(false); // Trạng thái modal

  useEffect(() => {
    const getOrder = async () => {
      try {
        const response = await fetch("http://localhost:6677/oder/getAllOrder");
        const result = await response.json();
        if (result.status) {
          const formattedOrder = result.data.map((orderItem) => ({
            id: orderItem._id,
            email: orderItem.cart[0]?.user?.name || "N/A",
            products: orderItem.cart[0]?.products || [], // Lưu toàn bộ sản phẩm
            unitPrice: `${orderItem.cart[0]?.products[0]?.price || 0}đ`,
            deliveryMethod: orderItem.ship === 1 ? "Nhanh" : "Chậm",
            orderStatus: getOrderStatus(orderItem.status),
            currentStatus: orderItem.status,
            totalProductPrice: `${orderItem.cart[0]?.total || 0}đ`,
            totalPayment: `${orderItem.totalOrder || 0}đ`,
          }));
          setOrder(formattedOrder);
        }
      } catch (error) {
        console.error("Error fetching orders:", error);
      }
    };
    getOrder();
  }, []);

  const getOrderStatus = (status) => {
    switch (status) {
      case 1:
        return "Chờ xác nhận";
      case 2:
        return "Đang vận chuyển";
      case 3:
        return "Hoàn thành";
      case 4:
        return "Đã hủy";
      default:
        return "Không xác định";
    }
  };

  const getOrderStatusColor = (status) => {
    switch (status) {
      case "Chờ xác nhận":
        return "orange";
      case "Đang vận chuyển":
        return "green";
      case "Hoàn thành":
        return "blue";
      case "Đã hủy":
        return "red";
      default:
        return "black";
    }
  };

  const updateOrderStatus = async (id, currentStatus) => {
    const nextStatus = currentStatus + 1;
    if (nextStatus > 3) {
      alert("Không thể cập nhật trạng thái này!");
      return;
    }

    try {
      const response = await fetch(`http://localhost:6677/oder/${id}/updateOrder`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ status: nextStatus }),
      });
      const result = await response.json();
      if (result.status) {
        setOrder((prevOrders) =>
          prevOrders.map((item) =>
            item.id === id
              ? {
                  ...item,
                  orderStatus: getOrderStatus(nextStatus),
                  currentStatus: nextStatus,
                }
              : item
          )
        );
        alert("Cập nhật trạng thái thành công!");
      } else {
        alert("Cập nhật trạng thái thất bại!");
      }
    } catch (error) {
      console.error("Error updating order status:", error);
      alert("Có lỗi xảy ra!");
    }
  };

  const handleUpdateClick = (order) => {
    setSelectedOrder(order); // Lưu đơn hàng được chọn
    setModalOpen(true); // Mở modal
  };

  const handleConfirmUpdate = () => {
    if (selectedOrder) {
      updateOrderStatus(selectedOrder.id, selectedOrder.currentStatus);
    }
    setModalOpen(false); // Đóng modal
  };

  const handleCancelUpdate = () => {
    setSelectedOrder(null); // Xóa đơn hàng được chọn
    setModalOpen(false); // Đóng modal
  };

  return (
    <div className="qlhh-container">
      <table className="order-table">
        <thead>
          <tr>
            <th>Tên người dùng</th>
            <th>Sản phẩm</th>
            <th>Mã đơn hàng</th>
            <th>Đơn giá</th>
            <th>Hình thức giao</th>
            <th>Trạng thái đơn hàng</th>
            <th>Tổng tiền SP</th>
            <th>Tổng thanh toán</th>
          </tr>
        </thead>
        <tbody>
          {order.map((item, index) => (
            <tr key={index}>
              <td style={{ color: "blue" }}>{item.email}</td>
              <td>
                {/* Hiển thị tất cả tên sản phẩm trong giỏ hàng, cách nhau bằng dấu phẩy */}
                {item.products.length > 0 ? (
                  item.products.map((product, idx) => (
                    <span key={idx}>
                      {product.name}
                      {idx < item.products.length - 1 ? ", " : ""}
                    </span>
                  ))
                ) : (
                  <div>Không có sản phẩm</div>
                )}
              </td>
              <td style={{ textAlign: "center" }}>
                <strong>{item.id}</strong>
              </td>
              <td style={{ textAlign: "center" }}>{item.unitPrice}</td>
              <td
                style={{
                  color: item.deliveryMethod === "Nhanh" ? "red" : "black",
                  textAlign: "center",
                }}
              >
                {item.deliveryMethod}
              </td>
              <td style={{ textAlign: "center", display: "flex", justifyContent: "center", alignItems: "center" }}>
                <span
                  style={{
                    color: getOrderStatusColor(item.orderStatus),
                    cursor: item.currentStatus < 4 ? "pointer" : "not-allowed",
                  }}
                  onClick={() => handleUpdateClick(item)}
                >
                  {item.orderStatus}
                </span>
              </td>
              <td style={{ textAlign: "center" }}>{item.totalProductPrice}</td>
              <td className="total-payment-cell">
                <div className="total-payment-text">{item.totalPayment}</div>
                <button
                  onClick={() => navigate(`/OrderDetail/${item.id}`)}
                  className="details-button"
                >
                  Chi tiết
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Modal */}
      {isModalOpen && (
        <div className="modal">
          <div className="modal-content">
            <p>
              Bạn có chắc chắn muốn cập nhật trạng thái đơn hàng <strong>{selectedOrder?.id}</strong>?
            </p>
            <button onClick={handleConfirmUpdate} className="confirm-button">
              Xác nhận
            </button>
            <button onClick={handleCancelUpdate} className="cancel-button">
              Hủy
            </button>
          </div>
        </div>
      )}
      <p className="luuy">
        *Lưu ý: Ấn vào ô trạng thái để cập nhật
      </p>
    </div>
  );
};

export default QLHH;
