const newChart = new Chart(ctx, {
  type: "pie",
  data: {
    labels: labels.map((label, index) => `${label} (${data[index]})`), // Hiển thị số lượng kế bên nhãn
    datasets: [
      {
        label: "Lượt bán",
        data: data,
        backgroundColor: [
          "rgba(255, 99, 132, 0.8)",
          "rgba(54, 162, 235, 0.8)",
          "rgba(75, 192, 192, 0.8)",
          "rgba(255, 206, 86, 0.8)",
          "rgba(153, 102, 255, 0.8)",
          "rgba(255, 159, 64, 0.8)",
          "rgba(201, 203, 207, 0.8)",
          "rgba(90, 203, 207, 0.8)",
        ],
        borderColor: [
          "rgba(255, 99, 132, 1)",
          "rgba(54, 162, 235, 1)",
          "rgba(75, 192, 192, 1)",
          "rgba(255, 206, 86, 1)",
          "rgba(153, 102, 255, 1)",
          "rgba(255, 159, 64, 1)",
          "rgba(201, 203, 207, 1)",
          "rgba(90, 203, 207, 1)",
        ],
        borderWidth: 1,
      },
    ],
  },
  options: {
    responsive: true,
    maintainAspectRatio: true,
    aspectRatio: 1.5,
    plugins: {
      legend: {
        position: "top",
        labels: {
          generateLabels: (chart) =>
            chart.data.labels.map((label, index) => ({
              text: `${label} (${chart.data.datasets[0].data[index]})`, // Thêm số lượng vào nhãn
              fillStyle: chart.data.datasets[0].backgroundColor[index],
            })),
        },
      },
      datalabels: {
        color: "#fff",
        font: {
          size: 12,
          weight: "bold",
        },
        formatter: (value) => `${value}`, // Hiển thị số lượng
      },
    },
  },
});
