import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import Swal from "sweetalert2";
import "./InsertProduct.css";

const UpdateProduct = () => {
  // Lấy id từ URL
  let { id } = useParams();
  const navigate = useNavigate();

  const [categories, setCategories] = useState([]);
  const [preserves, setPreserves] = useState([]);
  const [images, setImages] = useState([]);
  const [imageUrl, setImageUrl] = useState("");

  const [name, setName] = useState("");
  const [category, setCategory] = useState("");
  const [quantity, setQuantity] = useState("");
  const [origin, setOrigin] = useState("");
  const [price, setPrice] = useState("");
  const [fiber, setFiber] = useState("");
  const [oum, setOum] = useState("");
  const [preserve, setPreserve] = useState("");
  const [supplier, setSupplier] = useState("");
  const [uses, setUses] = useState("");
  const [description, setDescription] = useState("");

  // Fetch dữ liệu sản phẩm từ API
  useEffect(() => {
    const getProductDetail = async () => {
      try {
        const response = await fetch(
          `http://localhost:6677/products/getProductDetailById_App/${id}`
        );
        if (!response.ok) {
          throw new Error("Không thể lấy thông tin sản phẩm");
        }
        const result = await response.json();
        const product = result.products;
        setName(product.name);
        setCategory(product.category.category_id);
        setQuantity(product.quantity);
        setOrigin(product.origin);
        setPrice(product.price);
        setFiber(product.fiber);
        setOum(product.oum);
        setPreserve(product.preserve.preserve_id);
        setSupplier(product.supplier);
        setUses(product.uses);
        setImages(product.images);
        setDescription(product.description);
      } catch (error) {
        console.error("Lỗi khi lấy sản phẩm:", error);
      }
    };
    getProductDetail();
  }, [id]);

  // Lấy danh sách categories
  useEffect(() => {
    const getCategories = async () => {
      try {
        const response = await fetch("http://localhost:6677/categories");
        const result = await response.json();
        setCategories(result.data);
      } catch (error) {
        console.error("Lỗi khi lấy danh mục:", error);
      }
    };
    getCategories();
  }, []);

  // Lấy danh sách preserves
  useEffect(() => {
    const getPreserves = async () => {
      try {
        const response = await fetch("http://localhost:6677/preserves");
        const result = await response.json();
        setPreserves(result.data);
      } catch (error) {
        console.error("Lỗi khi lấy loại hàng:", error);
      }
    };
    getPreserves();
  }, []);

  // Upload ảnh lên Cloudinary
  const uploadToCloundinary = async () => {
    try {
      const file = document.getElementById("image").files[0];
      const data = new FormData();
      data.append("file", file);
      data.append("upload_preset", "ml_default");
      const response = await fetch(
        "https://api.cloudinary.com/v1_1/dffuzgy5h/image/upload",
        {
          method: "POST",
          body: data,
        }
      );
      const result = await response.json();
      setImages([...images, result.secure_url]);
    } catch (error) {
      console.error("Lỗi khi upload ảnh:", error);
      Swal.fire({
        icon: "error",
        title: "Lỗi",
        text: "Không thể upload ảnh!",
      });
    }
  };

  // Thêm ảnh từ link
  const handleAddImageUrl = () => {
    if (imageUrl) {
      setImages([...images, imageUrl]);
      setImageUrl(""); // Reset input
    }
  };

  // Xóa ảnh
  const removeImage = (img) => {
    const newImages = images.filter((item) => item !== img);
    setImages(newImages);
  };

  // Xử lý submit form
  const handleSubmit = async () => {
    try {
      // Kiểm tra dữ liệu
      if (
        !name ||
        !price ||
        !quantity ||
        images.length === 0 ||
        !category ||
        !preserve
      ) {
        Swal.fire({
          icon: "error",
          title: "Lỗi",
          text: "Vui lòng điền đầy đủ thông tin!",
        });
        return;
      }

      const body = {
        name: name,
        category: category,
        quantity: quantity,
        origin: origin || "", // Nếu không có nhập sẽ để rỗng
        price: price,
        fiber: fiber || "", // Nếu không có nhập sẽ để rỗng
        oum: oum || "", // Nếu không có nhập sẽ để rỗng
        preserve: preserve,
        supplier: supplier || "", // Nếu không có nhập sẽ để rỗng
        uses: uses || "", // Nếu không có nhập sẽ để rỗng
        images: images,
        description: description || "", // Nếu không có nhập sẽ để rỗng
      };
      const result = await fetch(
        `http://localhost:6677/products/${id}/update`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(body),
        }
      );
      const response = await result.json();
      if (response.status) {
        Swal.fire({
          icon: "success",
          title: "Thành công",
          text: "Sửa sản phẩm thành công",
        });
        // reset form
        setName("");
        setCategory("");
        setQuantity("");
        setOrigin("");
        setPrice("");
        setFiber("");
        setOum("");
        setPreserve("");
        setSupplier("");
        setUses("");
        setImages([]);
        setDescription("");
        navigate("/products");
      } else {
        Swal.fire({
          icon: "error",
          title: "Thất bại",
          text: "Sửa sản phẩm thất bại",
        });
      }
    } catch (error) {
      console.log("Lỗi:", error);
      Swal.fire({
        icon: "error",
        title: "Thất bại",
        text: "Sửa sản phẩm thất bại",
      });
    }
  };

  return (
    <div className="containerne">
      <form className="form-container">
        <div className="titleF">
          <h1>Sửa sản phẩm</h1>
        </div>
        <div className="n-c-q-x-container">
          <div className="mb-4 mt-4">
            <div className="inside-container">
              <label className="form-label">Tên sản phẩm:</label>
              <input
                type="text"
                className="form-control"
                placeholder="Enter name"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            </div>
          </div>
          <div className="mb-3">
            <div className="inside-container">
              <label className="form-label">Danh mục:</label>
              <select
                className="form-select"
                value={category}
                onChange={(e) => setCategory(e.target.value)}
              >
                {categories.map((item) => (
                  <option key={item._id} value={item._id}>
                    {item.name}
                  </option>
                ))}
              </select>
            </div>
          </div>
          <div className="mb-3">
            <div className="inside-container">
              <label className="form-label">Số lượng:</label>
              <input
                type="number"
                className="form-control"
                placeholder="Enter quantity"
                value={quantity}
                onChange={(e) => setQuantity(e.target.value)}
              />
            </div>
          </div>
          <div className="mb-3">
            <div className="inside-container">
              <label className="form-label">Xuất xứ:</label>
              <input
                type="text"
                className="form-control"
                placeholder="Enter description"
                value={origin}
                onChange={(e) => setOrigin(e.target.value)}
              />
            </div>
          </div>
        </div>

        <div className="g-d-c-l-container">
          <div className="mb-3">
            <div className="inside-container">
              <label className="form-label">Giá sản phẩm:</label>
              <input
                type="number"
                className="form-control"
                placeholder="Enter price"
                value={price}
                onChange={(e) => setPrice(e.target.value)}
              />
            </div>
          </div>
          <div className="mb-3">
            <div className="inside-container">
              <label className="form-label">Chất sơ:</label>
              <input
                type="text"
                className="form-control"
                placeholder="Enter fiber"
                value={fiber}
                onChange={(e) => setFiber(e.target.value)}
              />
            </div>
          </div>
          <div className="mb-3">
            <div className="inside-container">
              <label className="form-label">Đơn vị tính:</label>
              <input
                type="text"
                className="form-control"
                placeholder="Enter unit"
                value={oum}
                onChange={(e) => setOum(e.target.value)}
              />
            </div>
          </div>
        </div>

        <div className="n-c-a-container">
          <div className="mb-3">
            <div className="inside-container">
              <label className="form-label">Loại bảo quản:</label>
              <select
                className="form-select"
                value={preserve}
                onChange={(e) => setPreserve(e.target.value)}
              >
                {preserves.map((item) => (
                  <option key={item._id} value={item._id}>
                    {item.name}
                  </option>
                ))}
              </select>
            </div>
          </div>

          <div className="mb-3">
            <div className="inside-container">
              <label className="form-label">Nhà cung cấp:</label>
              <input
                type="text"
                className="form-control"
                placeholder="Enter supplier"
                value={supplier}
                onChange={(e) => setSupplier(e.target.value)}
              />
            </div>
          </div>

          <div className="mb-3">
            <div className="inside-container">
              <label className="form-label">Công dụng:</label>
              <input
                type="text"
                className="form-control"
                placeholder="Enter uses"
                value={uses}
                onChange={(e) => setUses(e.target.value)}
              />
            </div>
          </div>
        </div>

        <div className="image-upload-container">
          <div className="inside-container">
            <label className="form-label">Ảnh từ thiết bị:</label>
            <input
              type="file"
              className="form-controlimg"
              id="image"
              onChange={uploadToCloundinary}
            />
          </div>

          <div className="image-preview-container">
            {images.map((item, index) => (
              <div key={index} className="image-preview">
                <img src={item} alt="preview" />
                <button
                  onClick={() => removeImage(item)}
                  className="remove-image-btn"
                >
                  x
                </button>
              </div>
            ))}
          </div>
        </div>

        <div className="mb-3">
          <div className="inside-container">
            <label className="form-label">Mô tả:</label>
            <textarea
              className="form-controlD"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
          </div>
        </div>

        <div className="btn-container2">
          <button onClick={() => navigate("/products")} className="btn-primaryC">
            Hủy
          </button>
          <button onClick={handleSubmit} className="btn-primary">
            Sửa
          </button>
        </div>
      </form>
    </div>
  );
};

export default UpdateProduct;
