import React, { useState } from "react";
import "./PromotionManagement.css";
import edit from "../assets/images/insert.png";
import deleteRed from "../assets/images/deleteRed.png";
import {
    NotificationOutlined,
    UserOutlined,
    TagOutlined,
    BarChartOutlined,
    ReconciliationOutlined,
    MessageOutlined,
    SettingOutlined,
    PoweroffOutlined,
  } from "@ant-design/icons";

const PromotionManagementment = () => {
  const [data, setData] = useState([
    {
      title: "Christmas Sale",
      fixedDiscount: "10.000đ",
      percentageDiscount: "10%",
      minOrderValue: "null",
      createdDate: "2/12/2024",
      expiryDate: "3/12/2024",
    },
    {
      title: "Christmas Sale",
      fixedDiscount: "10.000đ",
      percentageDiscount: "10%",
      minOrderValue: "5",
      createdDate: "2/12/2024",
      expiryDate: "3/12/2024",
    },
    {
      title: "Christmas Sale",
      fixedDiscount: "10.000đ",
      percentageDiscount: "null",
      minOrderValue: "null",
      createdDate: "2/12/2024",
      expiryDate: "3/12/2024",
    },
  ]);

  const handleDelete = (index) => {
    const newData = [...data];
    newData.splice(index, 1);
    setData(newData);
  };

  const handleAdd = () => {
    const newRow = {
      title: "Christmas Sale",
      fixedDiscount: "10.000đ",
      percentageDiscount: "null",
      minOrderValue: "null",
      createdDate: "2/12/2024",
      expiryDate: "3/12/2024",
    };
    setData([...data, newRow]);
  };

  return (
    <div className="App">
      <div className="button-container">
        <button className="add-button" onClick={handleAdd}>
          Thêm mới
        </button>
      </div>
      <table>
        <thead>
          <tr>
            <th>Tiêu đề</th>
            <th>Số tiền giảm cố định</th>
            <th>Giảm theo phần trăm</th>
            <th>Giá trị đơn hàng tối thiểu</th>
            <th>Ngày Tạo</th>
            <th>Thời gian hết hạn khuyến mãi</th>
            <th>Tác vụ</th>
          </tr>
        </thead>
        <tbody>
          {data.map((row, index) => (
            <tr key={index}>
              <td>{row.title}</td>
              <td>{row.fixedDiscount}</td>
              <td>{row.percentageDiscount}</td>
              <td>{row.minOrderValue}</td>
              <td>{row.createdDate}</td>
              <td>{row.expiryDate}</td>
              <td>
                <div className="editDiv">
                  <div className="editIcon">
                    <img className="edit" src={edit} alt="Edit" />
                  </div>
                  <div
                    className="deleteIcon"
                    onClick={() => handleDelete(index)}
                  >
                    <img className="delete" src={deleteRed} alt="Delete" />
                  </div>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default PromotionManagementment;
