import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const OrderDetail = () => {
  const { id } = useParams(); // Lấy id từ URL
  const [order, setOrder] = useState(null); // Lưu thông tin đơn hàng
  const [loading, setLoading] = useState(true); // Trạng thái tải dữ liệu
  const [error, setError] = useState(null); // Trạng thái lỗi

  // Hàm gọi API lấy chi tiết đơn hàng
  const fetchOrderDetail = async () => {
    try {
      const response = await fetch(`http://localhost:6677/oder/${id}/getOrderById`);
      const result = await response.json();
      console.log("API Response:", result); // Thêm dòng này
      if (result.status) {
        setOrder(result.data[0]); // Lưu dữ liệu đơn hàng đầu tiên
      } else {
        setError("Không thể tải dữ liệu đơn hàng");
      }
    } catch (err) {
      setError("Có lỗi xảy ra khi gọi API");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchOrderDetail(); // Gọi API khi component mount
  }, [id]);

  if (loading) {
    return <p>Đang tải dữ liệu...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      <h1>Chi tiết đơn hàng</h1>

      {order && (
        <>
          <section>
            <h2>Thông tin người dùng</h2>
            <p><strong>Tên:</strong> {order.cart[0].user.name}</p>
            <p><strong>Số điện thoại:</strong> {order.address.user.phone}</p>
          </section>

          <section>
            <h2>Thông tin sản phẩm</h2>
            {order.cart[0].products.map((product) => (
              <div key={product._id} style={{ border: "1px solid #ddd", padding: "10px", marginBottom: "10px", borderRadius: "5px" }}>
                <p><strong>Tên sản phẩm:</strong> {product.name}</p>
                <p><strong>Danh mục:</strong> {product.category.category_name}</p>
                <p><strong>Giá:</strong> {product.price}đ</p>
                <p><strong>Số lượng:</strong> {product.quantity}</p>
                <div>
                  <strong>Hình ảnh:</strong>
                  <div style={{ display: "flex", gap: "10px", marginTop: "10px" }}>
                    {product.images.map((image, index) => (
                      <img key={index} src={image} alt={`Hình ảnh sản phẩm ${product.name}`} style={{ width: "100px", height: "100px", objectFit: "cover" }} />
                    ))}
                  </div>
                </div>
              </div>
            ))}
          </section>

          <section>
            <h2>Thông tin giao hàng</h2>
            <p><strong>Địa chỉ:</strong> {`${order.address.houseNumber}, ${order.address.alley}, ${order.address.quarter}, ${order.address.district}, ${order.address.city}, ${order.address.country}`}</p>
            <p><strong>Phương thức giao hàng:</strong> {order.ship === 10 ? "Nhanh" : "Chậm"}</p>
            <p><strong>Trạng thái đơn hàng:</strong> {getOrderStatus(order.status)}</p>
          </section>

          <section>
            <h2>Tổng tiền</h2>
            <p><strong>Tổng giá sản phẩm:</strong> {order.cart[0].total}đ</p>
            <p><strong>Tổng thanh toán:</strong> {order.totalOrder}đ</p>
          </section>
        </>
      )}
    </div>
  );
};

// Hàm chuyển trạng thái đơn hàng sang dạng mô tả
const getOrderStatus = (status) => {
  switch (status) {
    case 1: return "Đang xử lý";
    case 2: return "Đang giao";
    case 3: return "Hoàn thành";
    case 4: return "Đã hủy";
    default: return "Không xác định";
  }
};

export default OrderDetail;
