import React, { useEffect, useState } from "react";
import {
  Chart,
  CategoryScale,
  LinearScale,
  PieController,
  BarController,  // Đăng ký BarController cho biểu đồ cột
  BarElement,      // Đăng ký BarElement để vẽ cột
  ArcElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import ChartDataLabels from "chartjs-plugin-datalabels";

const TopProductsChart = () => {
  const getStartOfWeek = () => {
    const today = new Date();
    const dayOfWeek = today.getDay(); // 0 (Chủ nhật) đến 6 (Thứ bảy)
    const startOfWeek = new Date(today);
    startOfWeek.setDate(
      today.getDate() - (dayOfWeek === 0 ? 6 : dayOfWeek - 1)
    ); // Nếu Chủ nhật, lấy thứ Hai trước đó
    return startOfWeek.toISOString().split("T")[0]; // Trả về dạng 'YYYY-MM-DD'
  };

  const [selectedDate, setSelectedDate] = useState(getStartOfWeek());
  const [chartInstance, setChartInstance] = useState(null);
  const [dateRange, setDateRange] = useState("");
  const [labels, setLabels] = useState([]);
  const [data, setData] = useState([]);

  const calculateDateRange = (startDate) => {
    const start = new Date(startDate);
    const end = new Date(start);
    end.setDate(start.getDate() + 6);

    const formatDate = (date) =>
      `${date.getDate().toString().padStart(2, "0")}/${(date.getMonth() + 1)
        .toString()
        .padStart(2, "0")}/${date.getFullYear()}`;

    return `${formatDate(start)} - ${formatDate(end)}`;
  };

  useEffect(() => {
    Chart.register(
      CategoryScale,
      LinearScale,
      PieController,
      BarController,  // Đảm bảo BarController được đăng ký
      BarElement,     // Đảm bảo BarElement được đăng ký
      ArcElement,
      Title,
      Tooltip,
      Legend,
      ChartDataLabels // Đăng ký plugin
    );

    const fetchData = async () => {
      try {
        const query = `?date=${selectedDate}`;
        const res = await fetch(
          `http://localhost:6677/products/getTop10PW${query}`
        );
        const result = await res.json();

        const fetchedLabels = [];
        const fetchedData = [];
        result.data.forEach((element) => {
          fetchedLabels.push(element.name);
          fetchedData.push(Number(element.sold));
        });

        setLabels(fetchedLabels);
        setData(fetchedData);
        setDateRange(calculateDateRange(selectedDate));

        // If chart instance already exists, update it instead of creating new one
        if (chartInstance) {
          chartInstance.data.labels = fetchedLabels.map((label, index) => `${label} (${fetchedData[index]})`);
          chartInstance.data.datasets[0].data = fetchedData;
          chartInstance.update(); // Update chart without recreating
        } else {
          const ctx = document.getElementById("myChart").getContext("2d");

          const newChart = new Chart(ctx, {
            type: "pie", // Start with Pie chart
            data: {
              labels: fetchedLabels.map((label, index) => `${label} (${fetchedData[index]})`),
              datasets: [
                {
                  label: "Lượt bán",
                  data: fetchedData,
                  backgroundColor: [
                    "rgba(255, 99, 132, 0.8)",
                    "rgba(54, 162, 235, 0.8)",
                    "rgba(75, 192, 192, 0.8)",
                    "rgba(255, 206, 86, 0.8)",
                    "rgba(153, 102, 255, 0.8)",
                    "rgba(255, 159, 64, 0.8)",
                    "rgba(201, 203, 207, 0.8)",
                    "rgba(90, 203, 207, 0.8)",
                  ],
                  borderColor: [
                    "rgba(255, 99, 132, 1)",
                    "rgba(54, 162, 235, 1)",
                    "rgba(75, 192, 192, 1)",
                    "rgba(255, 206, 86, 1)",
                    "rgba(153, 102, 255, 1)",
                    "rgba(255, 159, 64, 1)",
                    "rgba(201, 203, 207, 1)",
                    "rgba(90, 203, 207, 1)",
                  ],
                  borderWidth: 1,
                },
              ],
            },
            options: {
              responsive: false, // Tắt responsive để kiểm soát kích thước canvas
              maintainAspectRatio: true,
              aspectRatio: 1, // Tỷ lệ cố định cho pie chart
              plugins: {
                legend: {
                  position: "right",
                  labels: {
                    generateLabels: (chart) =>
                      chart.data.labels.map((label, index) => ({
                        text: `${label}`,
                        fillStyle: chart.data.datasets[0].backgroundColor[index],
                      })),
                  },
                },
                datalabels: {
                  color: "#fff",
                  font: {
                    size: 12,
                    weight: "bold",
                  },
                  formatter: (value) => `${value}`, // Hiển thị số lượng trực tiếp trên biểu đồ
                },
              },
            },
          });

          setChartInstance(newChart);
        }
      } catch (error) {
        console.error("Error fetching or displaying data:", error);
      }
    };

    fetchData();
  }, [selectedDate, chartInstance]);

  const handleChartChange = (type) => {
    if (chartInstance) {
      chartInstance.destroy(); // Hủy biểu đồ cũ
      const ctx = document.getElementById("myChart").getContext("2d");

      const newChart = new Chart(ctx, {
        type: type, // Đổi loại biểu đồ (pie hoặc bar)
        data: {
          labels: labels.map((label, index) => `${label} (${data[index]})`),
          datasets: [
            {
              label: "Lượt bán",
              data: data,
              backgroundColor: [
                "rgba(255, 99, 132, 0.8)",
                "rgba(54, 162, 235, 0.8)",
                "rgba(75, 192, 192, 0.8)",
                "rgba(255, 206, 86, 0.8)",
                "rgba(153, 102, 255, 0.8)",
                "rgba(255, 159, 64, 0.8)",
                "rgba(201, 203, 207, 0.8)",
                "rgba(90, 203, 207, 0.8)",
              ],
              borderColor: [
                "rgba(255, 99, 132, 1)",
                "rgba(54, 162, 235, 1)",
                "rgba(75, 192, 192, 1)",
                "rgba(255, 206, 86, 1)",
                "rgba(153, 102, 255, 1)",
                "rgba(255, 159, 64, 1)",
                "rgba(201, 203, 207, 1)",
                "rgba(90, 203, 207, 1)",
              ],
              borderWidth: 1,
            },
          ],
        },
        options: {
          responsive: false, // Tắt responsive
          maintainAspectRatio: true,
          aspectRatio: 1, // Tỷ lệ cố định
          plugins: {
            legend: {
              position: "right",
              labels: {
                generateLabels: (chart) =>
                  chart.data.labels.map((label, index) => ({
                    text: `${label}`,
                    fillStyle: chart.data.datasets[0].backgroundColor[index],
                  })),
              },
            },
            datalabels: {
              color: "#fff",
              font: {
                size: 12,
                weight: "bold",
              },
              formatter: (value) => `${value}`,
            },
          },
        },
      });

      setChartInstance(newChart);
    }
  };

  return (
    <div
      style={{
        padding: 5,
        marginBottom: "20px",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
      }}
    >
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          width: "100%",
          maxWidth: "1000px",
        }}
      >
        <h1
          style={{
            paddingLeft: "120px",
            color: "#27AAE1",
            fontSize: "24px",
            fontWeight: "bold",
            textAlign: "center",
            textTransform: "uppercase",
            flex: 1,
          }}
        >
          Sản phẩm bán chạy trong ứng dụng
        </h1>

        <div style={{ display: "flex", justifyContent: "center" }}>
          <input
            type="date"
            value={selectedDate}
            onChange={(e) => setSelectedDate(e.target.value)}
            style={{
              padding: "10px",
              margin: "0 10px",
              borderRadius: "5px",
              border: "1px solid #ccc",
            }}
          />
        </div>
      </div>

      <div style={{ width: "100%", maxWidth: "800px", textAlign: "center" }}>
        <p style={{ color: "gray", fontWeight: "bold" }}>
          {dateRange} - Tổng số sản phẩm bán: {data.reduce((acc, val) => acc + val, 0)}
        </p>
        <canvas id="myChart" width="500" height="500"></canvas>
      </div>

      <div style={{  }}>
        <button
          onClick={() => handleChartChange("pie")}
          style={{
            padding: "10px 20px",
            backgroundColor: "#27AAE1",
            color: "#fff",
            border: "none",
            cursor: "pointer",
            borderRadius: "5px",
            marginRight: "10px",
          }}
        >
          Biểu đồ Tròn
        </button>
        <button
          onClick={() => handleChartChange("bar")}
          style={{
            padding: "10px 20px",
            backgroundColor: "#27AAE1",
            color: "#fff",
            border: "none",
            cursor: "pointer",
            borderRadius: "5px",
          }}
        >
          Biểu đồ Cột
        </button>
      </div>
    </div>
  );
};

export default TopProductsChart;
